<?phpif(session_id()==''){session_start();}if (!isset($_SESSION['token'])){$_SESSION['token'] = rand(1, 100);}$err = ''; //if(isset($_POST['signin']) && isset($_SESSION['token']) && $_SESSION['token']==$_POST['token']) {    include('includes/systemconfig.inc');  $p = mysqli_real_escape_string($db_connection,$_POST['accountpassword']);  $rs = mysqli_query($db_connection,'SELECT * FROM tblaccount WHERE username=\''.    mysqli_real_escape_string($db_connection,$_POST['username']).'\' AND     accountpassword=\''.$p.'\' limit 0,1');  $rw = mysqli_fetch_array($rs);  if (mysqli_num_rows($rs) == 1) {    $_SESSION['accountid'] = $rw['accountid'];    $_SESSION['typeid'] = GetData('SELECT typeid FROM tblaccount WHERE accountid='.$rw['accountid']);    header('location: dashboard/');    //exit(0);  } else {    $err =  '<br><span style="color:red; font-size:12pm;" class="blink">Invalid Username or Password</span>';  }} if(isset($_SESSION['accountid'])) {  include_once('includes/systemconfig.inc');  header('location: dashboard/');  exit(0);}$_SESSION['token'] = rand(1, 100);?><!DOCTYPE html><html lang="en"><head>  <meta charset="UTF-8">  <meta http-equiv="X-UA-Compatible" content="IE=edge">  <meta name="viewport" content="width=device-width, initial-scale=1.0">  <title>Login | Panel</title>  <!-- <link rel="shortcut icon" href="./img/svg/logo.svg" type="image/x-icon"> -->  <link rel="stylesheet" href="./css/style.min.css">  <style>    .blink {      animation: 2s linear infinite condemned_blink_effect;    }    @keyframes condemned_blink_effect {        0% {        visibility: hidden;        }        50% {        visibility: hidden;        }        100% {        visibility: visible;        }    }  </style>    <script>    function show_me() {      var x = document.getElementById("accountpassword");      if (x.type === "password") {      x.type = "text";      } else {      x.type = "password";      }    }  </script></head